import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';


 export default class App extends Component {

  state={
    Menu:[
      {id:"1",item:"Crep", price:"2.50 JD" },
      {id:"2",item:"Waffel", price:"3 JD" },
      {id:"3",item:"TEA", price:"1 JD" },
      {id:"4",item:"Coffee", price:"2.30 JD" },
      {id:"5",item:"cack", price:"2.30 JD" }],

        showMenu:false
    }

    render(){
      let menu =  this.state.Menu.map(men=>(
        <div key={men.id}>
        <h2>

          name : {men.item}
          </h2> 
          <p>price : {men.price}</p>
          <button onClick={this.handleAddItem}>
            add
          </button>
          </div>
      ))
      return(
        <div>
        {
          menu
          }
          </div>
      )
    }
 }
