import React, { Component } from 'react';
import './Order.css';
//import './Bill.css';

import axios from 'axios';

export default class Order extends Component {


    createOrder = ()=>{
        // const data={
        //     itemDesc:this.props.orderItems,
        //     itemPrice:this.props.totalPrice,
        //     itemImage:null
        // }
        var Data = {
          "itemDesc":"John",
          "itemPrice":30,
          "itemImage": "kkk.jpg"
          };
        axios.post('http://jsonplaceholder.typicode.com/posts',Data)
        .then(response=>{
            alert('Thank you ')
            this.props.clearOrder()
         console.log(response)  
         })
    }
  render() {

    return (
<div>
    
 
   
   
      <ul>
          {this.props.items.map(item=>{
              const img =item.itemImage!=null? {'background-image':` url(${item.itemImage})`}:{}
              return(
                  <li key={item.itemId} style={img}><div className="title">
                  Price {item.itemPrice} JD
                  
                  </div>
                 
                  </li>
          )})}
       
        
      </ul>
                <button onClick={this.createOrder}>Create order</button>
     
           <form class="form-horizontal"> 
<fieldset>

<legend>Creat Order</legend>

<div class="form-group">
  <label class="col-md-2 control-label" for="textinput">itemId</label>  
  <div class="col-md-2">
  <input id="textinput" name="textinput" type="text"  class="form-control input-md"/>
  </div>

</div>
<div class="form-group">
  <label class="col-md-4 control-label" for="textinput">itemDesc</label>  
  <div class="col-md-4">
  <input id="textinput" name="textinput" type="text"  class="form-control input-md"/>
    
  </div>
</div>

<div class="form-group">
  <label class="col-md-4 control-label" for="textinput">itemImage</label>  
  <div class="col-md-4">
  <input id="textinput" name="textinput" type="text" class="form-control input-md"/>
    
  </div>
</div>

<div class="form-group">
  <label class="col-md-4 control-label" for="singlebutton">Single Button</label>
  <div class="col-md-4">
    <button id="singlebutton" name="singlebutton" class="btn btn-primary"onClick={this.createOrder}>Button</button>
  </div>
</div>

</fieldset>
</form>

<aside className="profile-card"><h1 class="n">Your order total price :</h1>

  <div className="modal fade" id="myModal" role="dialog">
    <div className="modal-dialog">

      <div className="modal-content">
        <div className="modal-header">
        <button type="button" className="close" data-dismiss="modal">&times;</button>
          <h2 className="Tprice">{this.props.totalPrice} </h2> 
        </div>
<div className="modal-footer">
          <button type="button" className="btn btn-default" data-dismiss="modal">Close</button>
        </div>
        </div>
      
      </div>
    </div>
</aside>

                  </div>


    )
  }
}
