import React, { Component } from 'react';
// import './Menu.css';
//import './Bill.css';

import axios from 'axios';

export default class Order extends Component {


    createOrder = ()=>{
        // const data={
        //     itemDesc:this.props.orderItems,
        //     itemPrice:this.props.totalPrice,
        //     itemImage:null
        // }
        var Data = {
          "namitemDesce":"John",
          "itemPrice":30,
          "itemImage": "kkk.jpg"
          };
        axios.post('http://jsonplaceholder.typicode.com/posts',Data)
        .then(response=>{
            alert('Thank you ')
            this.props.clearOrder()
         console.log(response)  
         })
    }
  render() {

    return (
<div>
    <h2>

  your  order total Price {this.props.totalPrice}
    </h2>
      <ul>
          {this.props.items.map(item=>{
              const img =item.itemImage!=null? {'background-image':` url(${item.itemImage})`}:{}
              return(
                  <li key={item.itemId} style={img}><div className="title">
                  Price {item.itemPrice} JD
                  
                  </div>
                 
                  </li>
          )})}
       
        
      </ul>
                <button onClick={this.createOrder}>Create order</button>

           <form class="form-horizontal"> 
<fieldset>

<legend>Form Name</legend>

<div class="form-group">
  <label class="col-md-4 control-label" for="textinput">Text Input</label>  
  <div class="col-md-4">
  <input id="textinput" name="textinput" type="text" placeholder="placeholder" class="form-control input-md"/>
  <span class="help-block">help</span>  
  </div>

</div>
<div class="form-group">
  <label class="col-md-4 control-label" for="textinput">Text Input</label>  
  <div class="col-md-4">
  <input id="textinput" name="textinput" type="text" placeholder="placeholder" class="form-control input-md"/>
  <span class="help-block">help</span>  
  </div>
</div>

<div class="form-group">
  <label class="col-md-4 control-label" for="textinput">Text Input</label>  
  <div class="col-md-4">
  <input id="textinput" name="textinput" type="text" placeholder="placeholder" class="form-control input-md"/>
  <span class="help-block">help</span>  
  </div>
</div>

<div class="form-group">
  <label class="col-md-4 control-label" for="singlebutton">Single Button</label>
  <div class="col-md-4">
    <button id="singlebutton" name="singlebutton" class="btn btn-primary"onClick={this.createOrder}>Button</button>
  </div>
</div>

</fieldset>
</form>

                  </div>


    )
  }
}
