import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

export default class App extends Component {

  state = {
    Menu: [
      { id: "1", item: "Crep", price: "2.50 JD" },
      { id: "2", item: "Waffel", price: "3 JD" },
      { id: "3", item: "TEA", price: "1 JD" },
      { id: "4", item: "Coffee", price: "2.30 JD" },
      { id: "5", item: "cack", price: "2.30 JD" }
    ],

    showMenu: false,
    order: [],
  }

  handleAddItem = (item) => {
    const currentOrder = [...this.state.order];
    currentOrder.push(item);
    const menu = this.state.Menu.filter(men => men.id != item.id)
    this.setState({
      order: currentOrder,
      Menu: menu,

    })
  }
  render() {
    let menu = this.state.Menu.map(men => (
      <div key={men.id}>
        <h2>

          name : {men.item}
        </h2>
        <p>Price : {men.price}</p>
        <button onClick={() => this.handleAddItem(men)}>
          Add
          </button>
      </div>
    ))

    let order = this.state.order.map(item => (
      <div key={item.id}>
        <h2 className="i">

          name : {item.item}
        </h2>
        <p className="p">price : {item.price}</p>

      </div>
    ))
    return (
      <div  className='container'>
        <div className='item1'>
          <h1>soso</h1>
          <p>bla bla</p>
          <div>
            <h1>soso</h1>
            <p>bla bla</p>
            <div>
              <h1>soso</h1>
              <p>bla bla</p>
            </div>
          </div>
        </div>
        </div>
        )
      }
}
